package com.fenbi.blog.bean;

import java.util.Date;

public class Comment {
	
	private int id;
	private int userid;
	private int blogid;
	private String content;
	private Date createTime;
	private String username;
	private String blogTitle;
	public Comment() {
		super();
		// TODO Auto-generated constructor stub
	}
	
	
	public Comment(int id, int userid, int blogid, String content, Date createTime, String username, String blogTitle) {
		super();
		this.id = id;
		this.userid = userid;
		this.blogid = blogid;
		this.content = content;
		this.createTime = createTime;
		this.username = username;
		this.blogTitle = blogTitle;
	}
	
	@Override
	public String toString() {
		return "Comment [id=" + id + ", userid=" + userid + ", blogid=" + blogid + ", content=" + content
				+ ", createTime=" + createTime + ", username=" + username + ", blogTitle=" + blogTitle + "]";
	}

	
	public String getUsername() {
		return username;
	}


	public void setUsername(String username) {
		this.username = username;
	}


	public String getBlogTitle() {
		return blogTitle;
	}


	public void setBlogTitle(String blogTitle) {
		this.blogTitle = blogTitle;
	}


	public int getId() {
		return id;
	}
	public void setId(int id) {
		this.id = id;
	}
	public int getUserid() {
		return userid;
	}
	public void setUserid(int userid) {
		this.userid = userid;
	}
	public int getBlogid() {
		return blogid;
	}
	public void setBlogid(int blogid) {
		this.blogid = blogid;
	}
	public String getContent() {
		return content;
	}
	public void setContent(String content) {
		this.content = content;
	}
	public Date getCreateTime() {
		return createTime;
	}
	public void setCreateTime(Date createTime) {
		this.createTime = createTime;
	}
	
	

}
